#include "BootScreen.hpp"

BootScreen::~BootScreen()
{
}

void BootScreen::display() const
{
    int screen = _getRandomNumber(0, _availableScreens - 1);
    displayScreen(screen);
}

void BootScreen::displayAllScreens() const
{
    for (int i = 0; i < _availableScreens; i++) {
        displayScreen(i);
    }
}

void BootScreen::displayScreen(const unsigned int screen) const
{
    if (screen < 0 || screen >= _availableScreens) {
        return;
    }
    if (screen < _availableScreens) {
        for (int j = 0; j < _screens[screen].size(); j++) {
            std::cout << _screens[screen][j] << std::endl;
        }
    }
}

int BootScreen::getAvailableScreens() const
{
    return _availableScreens;
}

void BootScreen::_addScreen(const std::vector<std::string> &screen)
{
    _screens.push_back(screen);
    _availableScreens = _screens.size();
}

int BootScreen::_getRandomNumber(int min, int max) const
{
    static std::random_device rd;
    static std::mt19937 gen(rd());
    std::uniform_int_distribution<int> dist(min, max);
    return static_cast<int>(dist(gen));
}

